\[Beta][lambdaH, 2] = (449/16)*gSU2L^6 - 145/48*gSU2L^4*gU1Y^2 - 253/8*gSU2L^4*lambdaH - 3/4*gSU2L^4*Tr[ConjugateTranspose[Ynu].Ynu] - 287/48*gSU2L^2*gU1Y^4 + (39/4)*gSU2L^2*gU1Y^2*lambdaH - 1/2*gSU2L^2*gU1Y^2*Tr[ConjugateTranspose[Ynu].Ynu] + 108*gSU2L^2*lambdaH^2 + (15/2)*gSU2L^2*lambdaH*Tr[ConjugateTranspose[Ynu].Ynu] - 107/48*gU1Y^6 + (289/24)*gU1Y^4*lambdaH - 1/4*gU1Y^4*Tr[ConjugateTranspose[Ynu].Ynu] + 36*gU1Y^2*lambdaH^2 + (5/2)*gU1Y^2*lambdaH*Tr[ConjugateTranspose[Ynu].Ynu] - 312*lambdaH^3 - 48*lambdaH^2*Tr[ConjugateTranspose[Ynu].Ynu] - 5/2*lambdaH*lambdaP^2 - 12*lambdaH*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] - lambdaH*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu] - 3*lambdaH*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] - 1/2*lambdaP^3 - lambdaP^2*Tr[Conjugate[lambdaL].lambdaL] - lambdaP^2*Tr[Conjugate[lambdaR].lambdaR] - 1/2*lambdaP^2*Tr[ConjugateTranspose[lambdaN].lambdaN] + 8*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[Ynu].Conjugate[Ynu].lambdaR] + 8*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] + 10*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu] + 2*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN];
\[Beta][lambdaP, 2] = -325/16*gSU2L^4*lambdaP + (15/8)*gSU2L^2*gU1Y^2*lambdaP + 72*gSU2L^2*lambdaH*lambdaP + (3/2)*gSU2L^2*lambdaP^2 + (15/4)*gSU2L^2*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu] + (217/48)*gU1Y^4*lambdaP + 24*gU1Y^2*lambdaH*lambdaP + (1/2)*gU1Y^2*lambdaP^2 + (5/4)*gU1Y^2*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu] - 480*lambda1*Conjugate[lambda1]*lambdaP - 60*lambdaH^2*lambdaP - 36*lambdaH*lambdaP^2 - 24*lambdaH*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu] - 11/4*lambdaP^3 - 6*lambdaP^2*lambdaS - 4*lambdaP^2*Tr[Conjugate[lambdaL].lambdaL] - 4*lambdaP^2*Tr[Conjugate[lambdaR].lambdaR] - 2*lambdaP^2*Tr[ConjugateTranspose[Ynu].Ynu] - 2*lambdaP^2*Tr[ConjugateTranspose[lambdaN].lambdaN] - 5/2*lambdaP*lambdaS^2 - 8*lambdaP*lambdaS*Tr[Conjugate[lambdaL].lambdaL] - 8*lambdaP*lambdaS*Tr[Conjugate[lambdaR].lambdaR] - 4*lambdaP*lambdaS*Tr[ConjugateTranspose[lambdaN].lambdaN] - 24*lambdaP*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] - 24*lambdaP*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] - 12*lambdaP*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] + 14*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] - 9/2*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu] + (7/2)*lambdaP*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] - 24*lambdaP*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] - 3*lambdaP*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] - 24*lambdaP*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] - 12*lambdaP*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR] + 320*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 80*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR.ConjugateTranspose[lambdaN].lambdaN] + 64*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[Ynu].Conjugate[Ynu].lambdaR] + 80*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] + 80*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[lambdaN].Conjugate[lambdaN].lambdaR] + 112*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] + 28*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] + 80*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + 20*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 80*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 80*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR];
\[Beta][lambdaS, 2] = 12*gSU2L^2*lambdaP^2 + 4*gU1Y^2*lambdaP^2 - 4416*lambda1*Conjugate[lambda1]*lambdaS - 2304*lambda1*Conjugate[lambda1]*Tr[Conjugate[lambdaL].lambdaL] - 2304*lambda1*Conjugate[lambda1]*Tr[Conjugate[lambdaR].lambdaR] - 1152*lambda1*Conjugate[lambda1]*Tr[ConjugateTranspose[lambdaN].lambdaN] - 4*lambdaP^3 - 5*lambdaP^2*lambdaS - 4*lambdaP^2*Tr[ConjugateTranspose[Ynu].Ynu] - 15*lambdaS^3 - 20*lambdaS^2*Tr[Conjugate[lambdaL].lambdaL] - 20*lambdaS^2*Tr[Conjugate[lambdaR].lambdaR] - 10*lambdaS^2*Tr[ConjugateTranspose[lambdaN].lambdaN] + 16*lambdaS*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] + 16*lambdaS*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 8*lambdaS*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] - 24*lambdaS*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] - 6*lambdaS*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] + 16*lambdaS*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + 2*lambdaS*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 16*lambdaS*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 8*lambdaS*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR] + 1024*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] + 1024*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 768*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN.Conjugate[lambdaR].lambdaR] + 768*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.Conjugate[lambdaL].lambdaL.lambdaN] + 256*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 64*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR.ConjugateTranspose[lambdaN].lambdaN] + 64*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] + 64*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].Transpose[lambdaN].Conjugate[lambdaN].lambdaR] + 64*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + 16*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 64*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 64*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR] + 1536*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 384*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] + 192*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].Transpose[lambdaN].Conjugate[lambdaN].lambdaR] + 384*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + 32*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 1536*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN.Conjugate[lambdaR].lambdaR] + 384*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 1536*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL.lambdaN] + 768*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].Conjugate[lambdaN].lambdaR] + 768*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR.Conjugate[lambdaR].lambdaR] + 384*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR.ConjugateTranspose[lambdaN].lambdaN] + 192*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].Transpose[lambdaN].lambdaL.lambdaN];
\[Beta][lambda1, 2] = 192*lambda1^2*Conjugate[lambda1] - 5*lambda1*lambdaP^2 - 23*lambda1*lambdaS^2 - 24*lambda1*lambdaS*Tr[Conjugate[lambdaL].lambdaL] - 24*lambda1*lambdaS*Tr[Conjugate[lambdaR].lambdaR] - 12*lambda1*lambdaS*Tr[ConjugateTranspose[lambdaN].lambdaN] + 144*lambda1*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] + 144*lambda1*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 72*lambda1*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] - 24*lambda1*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] - 6*lambda1*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] + 144*lambda1*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + 18*lambda1*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 144*lambda1*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 72*lambda1*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR];


(* Scalar Mass Couplings *)

ScalarMassCouplings = {m1};

\[Beta][m1, 1] = 6*lambda1*m1 + 6*Conjugate[lambda1]*m1 + (1/2)*lambdaS*m1 + 2*m1*Tr[Conjugate[lambdaL].lambdaL] + 2*m1*Tr[Conjugate[lambdaR].lambdaR] + m1*Tr[ConjugateTranspose[lambdaN].lambdaN];

\[Beta][m1, 2] = 48*lambda1*Conjugate[lambda1]*m1 - 12*lambda1*lambdaS*m1 - 24*lambda1*m1*Tr[Conjugate[lambdaL].lambdaL] - 24*lambda1*m1*Tr[Conjugate[lambdaR].lambdaR] - 12*lambda1*m1*Tr[ConjugateTranspose[lambdaN].lambdaN] - 12*Conjugate[lambda1]*lambdaS*m1 - 24*Conjugate[lambda1]*m1*Tr[Conjugate[lambdaL].lambdaL] - 24*Conjugate[lambda1]*m1*Tr[Conjugate[lambdaR].lambdaR] - 12*Conjugate[lambda1]*m1*Tr[ConjugateTranspose[lambdaN].lambdaN] - 1/4*lambdaP^2*m1 - 3/4*lambdaS^2*m1 - 2*lambdaS*m1*Tr[Conjugate[lambdaL].lambdaL] - 2*lambdaS*m1*Tr[Conjugate[lambdaR].lambdaR] - lambdaS*m1*Tr[ConjugateTranspose[lambdaN].lambdaN] + 4*m1*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] + 4*m1*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 2*m1*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] - 6*m1*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] - 3/2*m1*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] + 4*m1*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + (1/2)*m1*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 4*m1*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 2*m1*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR];


(* Vacuum-expectation Values *)

Vevs = {vH, vS};

(* For vevs the gauge must be fixed. Let's use for instance the Landau gauge : *)
xiGauge = 0;

\[Beta][vH, 1] = (3/4)*xiGauge*gSU2L^2*vH + (1/4)*xiGauge*gU1Y^2*vH + (9/4)*gSU2L^2*vH + (3/4)*gU1Y^2*vH - vH*Tr[ConjugateTranspose[Ynu].Ynu];
\[Beta][vS, 1] = -2*vS*Tr[Conjugate[lambdaL].lambdaL] - 2*vS*Tr[Conjugate[lambdaR].lambdaR] - vS*Tr[ConjugateTranspose[lambdaN].lambdaN];

\[Beta][vH, 2] = (3/4)*xiGauge^2*gSU2L^2*gU1Y^2*vH + (1/8)*xiGauge^2*gU1Y^4*vH + (27/8)*xiGauge*gSU2L^4*vH + (3/4)*xiGauge*gSU2L^2*gU1Y^2*vH - 3/2*xiGauge*gSU2L^2*vH*Tr[ConjugateTranspose[Ynu].Ynu] + (1/8)*xiGauge*gU1Y^4*vH - 1/2*xiGauge*gU1Y^2*vH*Tr[ConjugateTranspose[Ynu].Ynu] + (451/32)*gSU2L^4*vH - 9/16*gSU2L^2*gU1Y^2*vH - 15/8*gSU2L^2*vH*Tr[ConjugateTranspose[Ynu].Ynu] - 91/96*gU1Y^4*vH - 5/8*gU1Y^2*vH*Tr[ConjugateTranspose[Ynu].Ynu] - 6*lambdaH^2*vH - 1/8*lambdaP^2*vH + 3*vH*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] + (9/4)*vH*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[Ynu].Ynu] + (3/4)*vH*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN];
\[Beta][vS, 2] = -48*lambda1*Conjugate[lambda1]*vS - 1/4*lambdaP^2*vS - 1/4*lambdaS^2*vS + 12*vS*Tr[Conjugate[lambdaL].lambdaL.Conjugate[lambdaL].lambdaL] + 12*vS*Tr[Conjugate[lambdaR].lambdaR.Conjugate[lambdaR].lambdaR] + 6*vS*Tr[Conjugate[lambdaR].Transpose[lambdaN].lambdaL.lambdaN] + 6*vS*Tr[ConjugateTranspose[Ynu].Ynu.Conjugate[lambdaR].lambdaR] + (3/2)*vS*Tr[ConjugateTranspose[Ynu].Ynu.ConjugateTranspose[lambdaN].lambdaN] + 12*vS*Tr[ConjugateTranspose[lambdaN].lambdaN.Conjugate[lambdaR].lambdaR] + (3/2)*vS*Tr[ConjugateTranspose[lambdaN].lambdaN.ConjugateTranspose[lambdaN].lambdaN] + 12*vS*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].lambdaL.lambdaN] + 6*vS*Tr[ConjugateTranspose[lambdaN].Conjugate[lambdaL].Conjugate[lambdaN].lambdaR];
(* ::Subsection:: *)
(*2. Solving and plotting functions*)

(* ::Subsubsection::Closed:: *)
(*RG-solving functions*)

(* Solving function *)

RGsolve[initialScale_, range_] := Block[{allCouplings, couplingsToFunction, differentialEquations, initialConditions, allEquations, solutions, yukawaLike},
	(* Define and solve the system of coupled differential equations *)
	allCouplings = Join[GaugeCouplings, YukawaCouplings, QuarticCouplings, ScalarMassCouplings, Vevs];
	couplingsToFunction = # -> #[t] & /@ allCouplings;

	differentialEquations = (#'[t] == (\[Beta][#] /. couplingsToFunction) & /@ allCouplings);
	initialConditions = (#[initialScale] == init[#] & /@ allCouplings);
	allEquations = Join[differentialEquations, initialConditions];

	solutions = NDSolve[allEquations, allCouplings /. couplingsToFunction, {t, range[[1]], range[[2]]}][[1]];

	(* Now split matrix couplings into their individual components *)
	yukawaLike = {{Ynu, {3, 3}}, {lambdaN, {3, 3}}, {lambdaR, {3, 3}}, {lambdaL, {3, 3}}};
	solutions = Block[{coupling, function, shape},
					coupling = #[[1,0]];
					function = #[[2]];
					If[MemberQ[Flatten@yukawaLike, coupling],
						shape = yukawaLike[[Position[yukawaLike, coupling][[1,1]], 2]];
						Sequence @@ Flatten @ Table[coupling[i,j] -> FunctionInterpolation[If[NumericQ[t], function[[i,j]]], {t, range[[1]], range[[2]]}][t], {i, shape[[1]]}, {j, shape[[2]]}],
						coupling -> function]]& /@ solutions;

	Return[solutions];
];

(* The following function removes from the list the couplings which seem to identically vanish over the energy range *)
DiscardZeroCouplings[range_, solutions_] := Block[{N, testPoints},
	N = 20;
	testPoints =  Range[range[[1]], range[[2]], (range[[2]] - range[[1]])/N];
	Return @ Select[solutions, (DeleteDuplicates[#[[2, 0]] /@ testPoints] != {0}) &];
];
(* ::Subsubsection::Closed:: *)
(*Plot function*)

(* Plotting function handling both real and complex quantities *)

Complexplot[funcs_, {min_, max_}, legends_, plotRange_] := Block[{cplxPos, newFuncs, newLegends},
	cplxPos = If[Head[Chop[funcs[[#]] /. t -> 1/2(max-min)]] === Complex,#, Nothing]& /@ Range[Length[funcs]];
	newFuncs = If[MemberQ[cplxPos, #], Sequence @@ {Re@funcs[[#]], Im@funcs[[#]]}, funcs[[#]]]& /@ Range[Length[funcs]];
	newLegends = If[MemberQ[cplxPos, #], Sequence @@ {Re@legends[[#]], Im@legends[[#]]}, legends[[#]]]& /@ Range[Length[funcs]];

	Return @ Plot[Evaluate @ newFuncs, {t, min, max}, PlotLegends->newLegends, PlotRange->plotRange]
 ]


RGplot[range_, solutions_] := Block[{selectType, toPlot},
	selectType[type_] := Select[solutions, MemberQ[type, If[Head@#[[1]] =!= Symbol, Head@#[[1]], #[[1]]]]&];

	toPlot = selectType[GaugeCouplings];
	If[toPlot =!= {},
		Print["### Gauge Couplings ###"];
		Print @ Complexplot[toPlot[[All, 2]], range, toPlot[[All, 1]], All];
	];

	toPlot = selectType[YukawaCouplings];
	If[toPlot =!= {},
		Print["### Yukawa Couplings ###"];
		Print @ Complexplot[toPlot[[All, 2]], range, toPlot[[All, 1]], All];
	];

	toPlot = selectType[QuarticCouplings];
	If[toPlot =!= {},
		Print["### Quartic Couplings ###"];
		Print @ Complexplot[toPlot[[All, 2]], range, toPlot[[All, 1]], All];
	];

	toPlot = selectType[ScalarMassCouplings];
	If[toPlot =!= {},
		Print["### Scalar Mass Couplings ###"];
		Print @ Complexplot[toPlot[[All, 2]], range, toPlot[[All, 1]], All];
	];

	toPlot = selectType[Vevs];
	If[toPlot =!= {},
		Print["### Vacuum-expectation Values ###"];
		Print @ Complexplot[toPlot[[All, 2]], range, toPlot[[All, 1]], All];
	];
];
(* ::Subsection:: *)
(*3. Actual solving*)


(* Some useful quantities. Energy and mass are in units of Log10[ GeV ] *)

MZ = 91.1876                 // Log10; (* Z boson mass *)
MP = 1.22*10^19              // Log10; (* Planck mass *)
Mp = 1.22*10^19 / Sqrt[8 Pi] // Log10; (* Reduced Planck mass *)

MT = 173.0 // Log10; (* Pole top mass *)

vev = 246.22 // Log10; (* SM Higgs vacuum-expectation value *)


(*** Initial conditions ; Energy range ***)

initialScale = MZ;
range = {0, MP};

(* Gauge Couplings *)

init[gU1Y] = 0;
init[gSU2L] = 0;

(* Yukawa Couplings *)

init[Ynu] = {{0, 0, 0},
             {0, 0, 0},
             {0, 0, 0}};

init[lambdaN] = {{0, 0, 0},
                 {0, 0, 0},
                 {0, 0, 0}};

init[lambdaR] = {{0, 0, 0},
                 {0, 0, 0},
                 {0, 0, 0}};

init[lambdaL] = {{0, 0, 0},
                 {0, 0, 0},
                 {0, 0, 0}};

(* Quartic Couplings *)

init[lambdaH] = 0;
init[lambdaP] = 0;
init[lambdaS] = 0;
init[lambda1] = 0;

(* Scalar Mass Couplings *)

init[m1] = 0;

(* Vacuum-expectation Values *)

init[vH] = 0;
init[vS] = 0;


(* Definition of the running scheme *)

(loops[#] = 3)& /@ GaugeCouplings;
(loops[#] = 2)& /@ YukawaCouplings;
(loops[#] = 2)& /@ QuarticCouplings;
(loops[#] = 2)& /@ ScalarMassCouplings;
(loops[#] = 2)& /@ Vevs;


(* Now call the functions defined in section 2 *)

solutions = RGsolve[initialScale, range];
solutions = DiscardZeroCouplings[range, solutions];

RGplot[range, solutions]